import Image from "../Images/pexels-vincent.jpg";
import "./Card.css";

export const Card=()=>{
    return(
        <div className="m-4">
            {/* <div className="md:h-48 h-80 p-3 shadow-xl rounded-md bg-gray-100 max-w-screen-lg m-auto "> */}
                <div className="card-box">
                {/* <div className="grid md:grid-cols-5 md:grid-rows-1 grid-rows-5 grid-cols-1"> */}
                <div className="card-grid">
                    <div className="user-pic">
                        <img src={Image} alt="user-pic" 
                        // className="rounded-full w-32 h-32 object-contain border-4 border-purple-400"/>
                        className="avatar"/>
                    </div>
                    {/* <div className=" md:col-start-2 md:col-end-6 md:row-start-1 row-start-2 row-end-6 "> */}
                    <div className="content">   
                        {/* <p className="m-2 md:m-4 font-bold text-3xl text-purple-500 "> */}
                        <p className="content-title">  
                            Lorem ipsum
                        </p>

                        {/* <p className="font-semibold text-gray-600 md:text-green-300 text-xl mt-1.5 line-clamp-3"> */}
                        <p className="content-para">
                        It is important to ensure that the contrast ratio between the color of the placeholder text and the background of the input is high enough that people experiencing low vision conditions will be able to read it while also making sure there is enough of a difference between the placeholder text and input text color that users do not mistake the placeholder for inputed data.
                        </p>
                    </div>
                </div>
            </div>
        </div>
    )
}
