import React,{useEffect,useState} from 'react';

export const Anime =()=>{


    const [image,setImage]=useState("#");
    const [description,setDescription]=useState("Description");
    const [query,setQuery]=useState("");
    const [anime,setAnime]=useState("Nothing lies here");

    const submitHandler=(e)=>{
        e.preventDefault();
        fetch("/anime", {
            method:"POST",
            cache: "no-cache",
            headers:{
                "content_type":"application/json",
            },
            body:JSON.stringify(query)
            }
        ).then(response => {

        return response.json()
      })
      .then(json => {

      setAnime(json.data.Media.title.native);
      setDescription(json.data.Media.description);
      setImage(json.data.Media.coverImage.medium);
      })
    }

    // useEffect(()=>{
    //     fetch('/anime').then(res=>res.json()).then(data=>{
    //         setAnime(data.data.Media.title.english);
    //     })
    // },[]);
    console.log(anime)
    return(
        <div className="container">
             <button className="btn btn-square" type="button" onclick={halfmoon.toggleDarkMode()}>
                <i className="fa fa-moon-o" aria-hidden="true"></i>
                <span className="sr-only">Toggle dark mode</span>
                </button>
            <div className="form-group text-center ">
            <input className="form-control"onChange={(e)=>setQuery(e.target.value)}></input>
            <button className="btn btn-primary"onClick={submitHandler}>Submit</button>
            </div>

            <div className="card p-4 text-center">
            <img className="img-fluid rounded"src={image} alt="No Image"></img>
            <div className="content">
            <p className="content-title">This  is {anime}</p>
            <p className="text-muted">{description}</p>
            </div>
            </div>

        </div>
    )


}
