import {motion } from "framer-motion"
import React, { useState } from 'react'
import {IoMenuSharp} from "react-icons/io5"

export const Menu=()=>{

    const [animate,setAnimate]=useState(flase);
    const boxVariant={
                hidden:{
                    x:animate? 110:0,
                },
                visible:{
                    x:0,


                }};

    const listVariant={
        hidden:{
            x:animate? 110:0,
            opacity:animate? 0:1
        },
        visible:{
            x:0,
            opacity:1,
            transition:{
                delay:0.5,
                staggerChildren:0.2

            }
        }
    };

    return(
        <div>
            <motion.ul className="w-16 h-32 m-4 bg-red-500"
            onClick={()=>setAnimate(!animate)}
            variants={boxVariant}
            initial="hidden"
            animate="visible">
                {[1,2,3].map(item=>{
                    return<motion.li 
                    className="w-10 h-5 my-2 ml-1 m bg-gray-200"
                    variants={listVariant}
                    initial="hidden"
                    animate="visible"> {item} </motion.li>
                })}
                <IoMenuSharp/>
            </motion.ul>
        </div>
    )
}
