import styled from '@emotion/styled';


export const Input=styled.input`
    /* padding:0 2rem 0 2rem; */
    width: 50px;
    margin: auto;
    border:1px solid #0000BF;
    background-color: white;
    color: gray;
    &:focus{
        background-color:greenyellow;
        outline: none;
    }
`
