import {motion } from "framer-motion"
import React, { useState } from 'react'


export const Menu=()=>{

    const [animate,setAnimate]=useState(false);
    const boxVariant={
                hidden:{
                    x:animate? "110vh":"0vh",
                },
                visible:{
                    x:0,
                    transition:{
                        when:"beforeChildren",
                        staggerChildren:0.2
                    }

                }};

    const listVariant={
        hidden:{
            x:-10,
            opacity:0
        },
        visible:{
            x:0,
            opacity:1,
            transition:{
                delay:0.5,
                staggerChildren:0.2

            }
        }
    };

    return(
        <div>
            <motion.ul className="w-16 h-32 m-4 bg-red-500"
            onClick={()=>setAnimate(!animate)}
            variants={boxVariant}
            initial="hidden"
            animate="visible">
                {[1,2,3].map(item=>{
                    return<motion.li 
                    className="w-10 h-5 my-2 ml-1 m bg-gray-200"
                    variants={listVariant}
                    initial="hidden"
                    animate="visible"> {item} </motion.li>
                })}
                hey
            </motion.ul>
        </div>
    )
}
